// Components (with EJ.js based layouts)
@import 'components/c-btn';
@import 'components/c-logo';
@import 'components/c-nav/c-nav';
@import 'components/c-tab/c-tab';
@import 'components/c-quote';
@import 'components/c-pager/c-pager';
@import 'components/c-accordion';

// Component Partial Naming Examples
// @import 'components/btn--icon';
// @import 'components/btn--accordion';
// @import 'components/menu-main';
// @import 'components/menu-mobile';
// @import 'components/menu-subfooter';
// @import 'components/masthead';
// @import 'components/summary';
// @import 'components/progress';
// @import 'components/action';
// @import 'components/person';
// @import 'components/ad';
// @import 'components/alert';
// @import 'components/listing';
// @import 'components/tabs';
// @import 'components/toggle';
// @import 'components/message';
// @import 'components/calendar';
// @import 'components/carousel'; <-- Boo ;(
// @import 'components/card';
// @import 'components/callout';
// @import 'components/cart';

%c-btn--core {
  display: inline-block;
  border: 1px solid $white;
  border-radius: rem(4px);
  padding: ru(0.5) ru(1);
  text-transform: uppercase;
  text-decoration: none;
}

%c-btn,
.c-btn {
  @extend %c-btn--core;
  border-color: palette(grey, mediumlight);
  background-color: $white;
  color: palette(grey);

  &:hover,
  &:focus,
  &:active {
    border-color: palette(grey);
    background-color: palette(grey, xlight);
  }
}

/*doc
---
title: Button Color Variations
name: c-btn-200
category: Components - Buttons
---

```html_example
<div class="sg__wrapper--light-grey"> <!--styleguide only -->
  <a href="#" class="c-btn--grey">Grey Button</a>
</div>
```
*/

%c-btn--grey,
.c-btn--grey {
  @extend %c-btn--core;
  background-color: palette(grey);
  color: $white;

  &:hover,
  &:focus,
  &:active {
    border-color: palette(grey);
    background-color: $white;
    color: palette(grey);
  }
}

/*doc
---
title: Button Size Variations
name: c-btn-300
category: Components - Buttons
---

```html_example
<div class="sg__wrapper--light-grey"> <!--styleguide only -->
  <a href="#" class="c-btn--grey c-btn--thin">Thin Grey Button</a>
</div>
```
*/

%c-btn--thin,
.c-btn--thin {
  @extend %c-btn--core;
  padding: ru(0.125) ru(1);
}

/*doc
---
title: Buttons - Inline Icons
name: c-btn-400
category: Components - Buttons
---

```html_example
<a href="#" class="c-btn--left"><svg class="svg"><use xlink:href="../svg/sprite-ui.svg#arrow-left"></use></svg>Back Button</a>
<a href="#" class="c-btn--right">Continue Button<svg class="svg"><use xlink:href="../svg/sprite-ui.svg#arrow-right"></use></svg></a>
```
*/

%c-btn--icon {
  @extend %c-btn;
  position: relative;
  border-color: palette(grey, mediumlight);

  &:hover,
  &:focus,
  &:active {
    background-color: palette(grey, xlight);
    color: palette(grey);
  }

  svg {
    // color: palette(green);
  }
}

%c-btn--left,
.c-btn--left {
  @extend %c-btn--icon;
  // See config/mixins/svg-position.scss
  @include svg-inline(
    $baseline: rem($base-line-height), // font baseline
    $svg-width: ru(0.75), // SVG width (units same as typography)
    $svg-height: ru(0.75), // SVG height
    $svg-pad-right: ru(0.25), // padding on right side of SVG
    $svg-pad-left: 0, // padding on left side of SVG
    $container-vertical-padding: ru(0.5), // Top & bottom padding for container
    $container-padding-left: ru(1), // Left padding for container
    $container-padding-right: ru(1), // Right padding for container    $svg-class: 'svg', // Class for the SVG inside the parent
    $h-alignment: 'left', // Align Icon on 'left' or 'right'
    $v-alignment: 'top'); // Align Icon 'middle' or 'top'
}

%c-btn--right,
.c-btn--right {
  @extend %c-btn--icon;
  @include svg-inline(
    $baseline: rem($base-line-height), // font baseline
    $svg-width: ru(0.75), // SVG width (units same as typography)
    $svg-height: ru(0.75), // SVG height
    $svg-pad-right: 0, // padding on left side of SVG
    $svg-pad-left: ru(0.5), // padding on right side of SVG
    $container-vertical-padding: ru(0.5), // Top & bottom padding for container
    $container-padding-left: ru(1), // Left padding for container
    $container-padding-right: ru(0.75), // Right padding for container
    $svg-class: 'svg', // Class for the SVG inside the parent
    $h-alignment: 'right', // Align Icon on 'left' or 'right'
    $v-alignment: 'top'); // Align Icon 'middle' or 'top'

  svg {
    position: absolute;
    top: ru(2 / 3);
    right: ru(1 / 3);
  }
}

/*doc
---
title: Buttons - Icons Only
name: c-btn-500
category: Components - Buttons
---

```html_example
<div class="sg__wrapper--light-grey"> <!--styleguide only -->
  <a href="#" class="c-btn--svg">
  <svg class="svg"><use xlink:href="../svg/sprite-ui.svg#close"></use></svg>
  <div class="u-text-hide">Close</div>
  </a>

  <a href="#" class="c-btn--svg">
  <svg class="svg"><use xlink:href="../svg/sprite-ui.svg#menu"></use></svg>
  <div class="u-text-hide">Menu</div>
  </a>
</div>

```
*/

%c-btn--svg,
.c-btn--svg {
  // See config/mixins/svg-position.scss
  @include svg-centered(ru(1), ru(1), ru(1.5), ru(1.5), 'svg');
  display: block;
  color: palette(grey, dark);

  &:hover,
  &:focus,
  &:active {
    background-color: palette(grey, light);
  }
}

%c-btn--svg--lrg,
.c-btn--svg--lrg {
  @extend %c-btn--svg;
  @include svg-centered(ru(1.25), ru(1.25), ru(1.25), ru(1.25), 'svg');
}

%c-btn--svg--sml,
.c-btn--svg--sml {
  @extend %c-btn--svg;
  @include svg-centered(ru(1), ru(1), ru(1), ru(1), 'svg');
}

%btn--menu,
.btn--menu {
  position: absolute;
  right: ru(1);
  bottom: ru(0.5);
  margin-bottom: 0;
  cursor: pointer;
  padding: ru(0.75) ru(0.25);
  max-height: 100%;

  @include respond-to('type-large') {
    bottom: 0;
  }

  @include respond-to('full-nav') {
    display: none;
  }

  &:hover,
  &:active,
  &:focus {
    text-decoration: none;
    color: $white;
  }
}

%c-tabs,
.c-tabs {
  margin-top: ru(1);
}

%c-tabs__list,
.c-tabs__list {
  @extend %u-list--reset;
  margin: 0;
  padding: 0;
}

%c-tabs__list-item,
.c-tabs__list li {
  display: inline-block;
  margin: 0;
}

%c-tabs__btn,
.c-tabs a {
  @include font-size($p--small-font-sizes);
  display: block;
  border: 1px solid palette(grey, light);
  background-color: palette(grey, mediumlight);
  padding: ru(0.25) ru(0.5);
  color: palette(grey, dark);
  font-weight: 600;

  &:hover,
  &:focus,
  &:active,
  &.is-active {
    background-color: $white;
  }
}

%c-pager,
.c-pager {
  @extend %u-clearfix;
  padding: ru(1) 0;
  text-align: center;

  @include respond-to('type-large') {
    padding-right: ru(1);
    padding-left: ru(1);
  }

  @include respond-to('full-nav') {
    padding-right: ru(0.5);
    padding-left: ru(0.5);
  }

  @include respond-to('max-size') {
    padding-right: 0;
    padding-left: 0;
  }
}

%c-pager__item-btn {
  display: inline-block;
  padding: 0 ru(1);
}

%c-pager__item-current,
.c-pager__item-current {
  @extend %c-pager__item-btn;
}

.c-pager__item-prev {
  @extend %c-pager__item-btn;
}

.c-pager__item-next {
  @extend %c-pager__item-btn;
}

// EXTENDS

// Core list wrapper
%c-nav--core {
  margin: 0;
  padding: 0;
  list-style: none;
}

// Core list item
%c-nav__item--core {
  display: block;
  margin: 0;
}

@import 'c-nav--vertical';
@import 'c-nav--horizontal';
@import 'c-nav--inline';

%c-nav--vertical,
.c-nav--vertical {
  @extend %c-nav--core;
}

%c-nav--vertical__item,
.c-nav--vertical__item {
  @extend %c-nav__item--core;
}

%c-nav--vertical__link,
.c-nav--vertical__link {
  display: block;
  padding: ru(0.25) 0;
  text-transform: uppercase;
  color: palette(grey);

  &:hover,
  &:focus,
  &:active {
    background-color: palette(grey, xlight);
    color: palette(grey, dark);
  }

  &.is-active {
    color: palette(grey, dark);
    font-weight: 700;
  }
}

%c-nav--horizontal,
.c-nav--horizontal {
  @extend %c-nav--core;
}

%c-nav--horizontal__item,
.c-nav--horizontal__item {
  @extend %c-nav__item--core;
  display: inline-block;

  &:not(:first-child) {
    padding-left: 1px;
  }

  &:not(:last-child) {
    padding-right: ru(0.25);
  }
}

%c-nav--horizontal__link,
.c-nav--horizontal__link {
  display: block;
  padding-top: 0;
  color: palette(grey);

  &:hover,
  &:focus,
  &:active {
    text-decoration: underline;
    color: palette(grey, dark);
  }

  &.is-active {
    color: palette(grey, dark);
  }
}

%c-nav--inline,
.c-nav--inline {
  @extend %u-list--reset;
  margin: 0;
  padding: 0;
  list-style: none;
  color: palette(grey);

  li {
    display: inline-block;
    margin: 0;

    &:not(:last-of-type)::after {
      padding-right: 3px;
      padding-left: ru(0.25);
      content: '»';
    }
  }

  a {
    color: palette(grey);

    &:hover,
    &:focus,
    &:active,
    &.is-active {
      text-decoration: underline;
      color: palette(grey, dark);
    }
  }
}

