@import 'global/vendor/normalize';
@import 'global/g-type';
@import 'global/g-link';
@import 'global/g-list';
@import 'global/g-svg';
@import 'global/g-form';
@import 'global/g-image';
@import 'global/g-table';
// @import 'global/print';

@viewport {
  width: device-width;
}

// LOAD FONTS
// @include font-face('My Font', $fonts-medium-italic, bold, italic);

// @include enable-ligatures();

// Set Base Fonts
// scss-lint:disable VendorPrefix
html {
  background-color: $white;
  line-height: strip-unit($base-line-height / $base-font-size);
  color: palette(grey, dark);
  font-family: $base-font-family;
  font-size: 100%;
}
// scss-lint:enable VendorPrefix

// Global Extends for Typography

%g-type__core {
  // Fade in fonts using Web Font Loader default classes https://github.com/at-import/toolkit#font-helpers
  @include content-fade-in(0.25s, '.wf-loading', false);
  // font-family: $base-font-family;
}

