// Text & Similar Inputs
// scss-lint:disable QualifyingElement
textarea,
select,
input[type='date'],
input[type='datetime'],
input[type='datetime-local'],
input[type='email'],
input[type='month'],
input[type='number'],
input[type='password'],
input[type='search'],
input[type='tel'],
input[type='text'],
input[type='time'],
input[type='url'],
input[type='week'] {
  @extend %g-input--core;
  background-color: $white;

  &.g-input--large {
    @extend %g-input--large;
  }

  &.g-input--large--centered {
    @extend %g-input--large--centered;
  }
}
//scss-lint:enable QualifyingElement


// Removes select drop-down arrow
//scss-lint:disable ImportantRule
select {
  @extend %g-input--core;
  background: url('../svg/sprite-ui/arrow-down.svg') no-repeat right ru(0.5)center;
  padding: ru(0.375) ru(1.5) ru(0.375) ru(0.5);
  -moz-appearance: none !important;
}
//scss-lint:enable ImportantRule

// Placeholder Note we're repeating it on purpose!
// a group of selectors containing an invalid selector is invalid.
// http://stackoverflow.com/questions/2610497/change-an-inputs-html5-placeholder-color-with-css
@mixin placeholder--core {
  color: palette(grey);
}

//scss-lint:disable VendorPrefix
input::-webkit-input-placeholder,
textarea::-webkit-input-placeholder {  // Separate rule for Firefox. Separate rule for IE, too. Cannot stack with WebKit's.
  @include placeholder--core;
}

input:-moz-placeholder,
textarea:-moz-placeholder {
  @include placeholder--core;
}

input::-moz-placeholder,
textarea::-moz-placeholder {
  @include placeholder--core;
}

input:-ms-input-placeholder,
textarea:-ms-input-placeholder {
  @include placeholder--core;
}
//scss-lint:enable VendorPrefix

textarea {
  height: ru(6);
  overflow: auto;
  resize: vertical;
}

optgroup {

  &::-moz-focus-inner {  // Kill phantom spacing and dotted border that appears in Firefox.
    border: 0;
    padding: 0;
  }
}

.form-required {
  font-weight: normal;
}

// input.placeholder_text,
// textarea.placeholder_text {
// }

%g-button--reset {
  border: 0;
  background-color: transparent;
  width: auto;
  border-spacing: 0;
  text-align: left;
  text-indent: 0;
  text-decoration: none;

  &::-moz-focus-inner,
  &::-moz-focus-inner {
    border: 0;
    padding: 0;
  }
}

//scss-lint:disable QualifyingElement
button,
input[type='reset'],
input[type='submit'],
input[type='button'] {
  @extend %g-button--reset;
  @extend %g-input--core;
  @extend %c-btn;
}
//scss-lint:enable QualifyingElement

// Search Input
///////////////
// Remove decoration from input
//scss-lint:disable QualifyingElement
input[type='search']::-webkit-search-decoration {  // UI Consistency
  display: none;
}
//scss-lint:enable QualifyingElement

// Radios & checkmarks
//////////////////////
// Remove shadows
//scss-lint:disable QualifyingElement
input[type='file'],
input[type='radio'],
input[type='checkbox'] {

  &:focus,
  &:active {
    box-shadow: none;
  }
}
//scss-lint:enable QualifyingElement

// Checkbox and radios (Drupal Styles)
%g-form__checkbox-radio-group--core {
  @extend %u-clearfix;
  margin-bottom: ru(2);
}

// Wrapper for each item
%g-form__checkbox-radio__item {
  position: relative;
  float: left;
  width: 100%;
}

// Element & label common styles
%g-form-check-radio--core {
  display: block;
  margin-top: ru(0.5);
  margin-bottom: (1);
  vertical-align: baseline;
  font-family: $base-font-family;
}

// Checkbox or radio UI component
%g-form-check-radio-element {
  @extend %g-form-check-radio--core;
  position: absolute;
  width: ru(1);
  height: ru(1);
}

// Label for each UI component
%g-form-check-radio-label {
  @extend %g-form-check-radio--core;
  float: left;
  padding-left: ru(1.5);
}

