td,
th {
  padding: 0;
}

// Components (with EJ.js based layouts)
@import 'components/c-btn/c-btn';
@import 'components/c-logo';
@import 'components/c-nav/c-nav';
@import 'components/c-tab/c-tab';
@import 'components/c-quote';
@import 'components/c-accordion/c-accordion';
@import 'components/c-pager/c-pager';

// Component Partial Naming Examples
// @import 'components/btn--icon';
// @import 'components/btn--accordion';
// @import 'components/menu-main';
// @import 'components/menu-mobile';
// @import 'components/menu-subfooter';
// @import 'components/masthead';
// @import 'components/summary';
// @import 'components/progress';
// @import 'components/action';
// @import 'components/person';
// @import 'components/ad';
// @import 'components/alert';
// @import 'components/listing';
// @import 'components/tabs';
// @import 'components/toggle';
// @import 'components/message';
// @import 'components/calendar';
// @import 'components/carousel'; <-- Boo ;(
// @import 'components/card';
// @import 'components/callout';
// @import 'components/cart';

// Mixins (none)


// Core Extendables
%c-btn--core {
  display: inline-block;
  border-width: 1px;
  border-style: solid;
  border-color: none;
  border-radius: rem(4px);
  text-transform: uppercase;
  text-decoration: none;
}


// Import Variations
@import 'c-btn--default';
@import 'c-btn--icon';

////
// Size Varations
////

%c-btn--default-padding {
  padding: ru(0.5) ru(1);
}

%c-btn--narrow-padding {
  padding: ru(0.25) ru(0.75);
}

////
// Color Variations
////
%c-btn--palette-grey {
  border-color: palette(grey, mediumlight);
  background-color: $white;
  color: palette(grey);

  &:hover,
  &:focus,
  &:active {
    border-color: palette(grey);
    background-color: palette(grey, xlight);
  }
}

%c-btn--palette-blue {
  border-color: palette(blue, dark);
  background-color: palette(blue);
  color: $white;

  &:hover,
  &:focus,
  &:active {
    border-color: palette(blue, dark);
    background-color: palette(blue, dark);
  }
}


// Button Variations
%c-btn,
.c-btn {
  @extend %c-btn--core;
  @extend %c-btn--default-padding;
  @extend %c-btn--palette-grey;
}

%c-btn--blue,
.c-btn--blue {
  @extend %c-btn--core;
  @extend %c-btn--default-padding;
  @extend %c-btn--palette-blue;
}

%c-btn--thin,
.c-btn--thin {
  @extend %c-btn--core;
  @extend %c-btn--narrow-padding;
  @extend %c-btn--palette-grey;
}

%c-btn--thin-blue,
.c-btn--thin-blue {
  @extend %c-btn--core;
  @extend %c-btn--narrow-padding;
  @extend %c-btn--palette-blue;
}

%c-btn--icon {
  @extend %c-btn;
  position: relative;
  border-color: palette(grey, mediumlight);

  &:hover,
  &:focus,
  &:active {
    background-color: palette(grey, xlight);
    color: palette(grey);
  }
}

%c-btn--left,
.c-btn--left {
  @extend %c-btn--icon;
  // See config/mixins/svg-position.scss
  @include svg-inline(
    $baseline: rem($base-line-height), // font baseline
    $svg-width: ru(0.75), // SVG width (units same as typography)
    $svg-height: ru(0.75), // SVG height
    $svg-pad-right: ru(0.25), // padding on right side of SVG
    $svg-pad-left: 0, // padding on left side of SVG
    $container-vertical-padding: ru(0.5), // Top & bottom padding for container
    $container-padding-left: ru(1), // Left padding for container
    $container-padding-right: ru(1), // Right padding for container    $svg-class: 'svg', // Class for the SVG inside the parent
    $h-alignment: 'left', // Align Icon on 'left' or 'right'
    $v-alignment: 'top'); // Align Icon 'middle' or 'top'
}

%c-btn--right,
.c-btn--right {
  @extend %c-btn--icon;
  @include svg-inline(
    $baseline: rem($base-line-height), // font baseline
    $svg-width: ru(0.75), // SVG width (units same as typography)
    $svg-height: ru(0.75), // SVG height
    $svg-pad-right: 0, // padding on left side of SVG
    $svg-pad-left: ru(0.5), // padding on right side of SVG
    $container-vertical-padding: ru(0.5), // Top & bottom padding for container
    $container-padding-left: ru(1), // Left padding for container
    $container-padding-right: ru(0.75), // Right padding for container
    $svg-class: 'svg', // Class for the SVG inside the parent
    $h-alignment: 'right', // Align Icon on 'left' or 'right'
    $v-alignment: 'top'); // Align Icon 'middle' or 'top'

  svg {
    position: absolute;
    top: ru(2 / 3);
    right: ru(1 / 3);
  }
}

%c-tabs,
.c-tabs {
  margin-top: ru(1);
}

%c-tabs__list,
.c-tabs__list {
  @extend %u-list--reset;
  margin: 0;
  padding: 0;
}

%c-tabs__list-item,
.c-tabs__list li {
  display: inline-block;
  margin: 0;
}

%c-tabs__btn,
.c-tabs a {
  @include font-size($p--small-font-sizes);
  display: block;
  border: 1px solid palette(grey, light);
  background-color: palette(grey, mediumlight);
  padding: ru(0.25) ru(0.5);
  color: palette(grey, dark);
  font-weight: 600;

  &:hover,
  &:focus,
  &:active,
  &.is-active {
    background-color: $white;
  }
}

%c-pager,
.c-pager {
  @extend %u-clearfix;
  padding: ru(1) 0;
  text-align: center;

  @include respond-to('type-large') {
    padding-right: ru(1);
    padding-left: ru(1);
  }

  @include respond-to('full-nav') {
    padding-right: ru(0.5);
    padding-left: ru(0.5);
  }

  @include respond-to('max-size') {
    padding-right: 0;
    padding-left: 0;
  }
}

%c-pager__item-btn {
  display: inline-block;
  padding: 0 ru(1);
}

%c-pager__item-current,
.c-pager__item-current {
  @extend %c-pager__item-btn;
}

.c-pager__item-prev {
  @extend %c-pager__item-btn;
}

.c-pager__item-next {
  @extend %c-pager__item-btn;
}

// EXTENDS

// Core list wrapper
%c-nav--core {
  margin: 0;
  padding: 0;
  list-style: none;
}

// Core list item
%c-nav__item--core {
  display: block;
  margin: 0;
}

@import 'c-nav--vertical';
@import 'c-nav--horizontal';
@import 'c-nav--inline';

%c-nav--vertical,
.c-nav--vertical {
  @extend %c-nav--core;
}

%c-nav--vertical__item,
.c-nav--vertical__item {
  @extend %c-nav__item--core;
}

%c-nav--vertical__link,
.c-nav--vertical__link {
  display: block;
  padding: ru(0.25) 0;
  text-transform: uppercase;
  color: palette(grey);

  &:hover,
  &:focus,
  &:active {
    background-color: palette(grey, xlight);
    color: palette(grey, dark);
  }

  &.is-active {
    color: palette(grey, dark);
    font-weight: 700;
  }
}

%c-nav--horizontal,
.c-nav--horizontal {
  @extend %c-nav--core;
}

%c-nav--horizontal__item,
.c-nav--horizontal__item {
  @extend %c-nav__item--core;
  display: inline-block;

  &:not(:first-child) {
    padding-left: 1px;
  }

  &:not(:last-child) {
    padding-right: ru(0.25);
  }
}

%c-nav--horizontal__link,
.c-nav--horizontal__link {
  display: block;
  padding-top: 0;
  color: palette(grey);

  &:hover,
  &:focus,
  &:active {
    text-decoration: underline;
    color: palette(grey, dark);
  }

  &.is-active {
    color: palette(grey, dark);
  }
}

%c-nav--inline,
.c-nav--inline {
  @extend %u-list--reset;
  margin: 0;
  padding: 0;
  list-style: none;
  color: palette(grey);

  li {
    display: inline-block;
    margin: 0;

    &:not(:last-of-type)::after {
      padding-right: 3px;
      padding-left: ru(0.25);
      content: 'Â»';
    }
  }

  a {
    color: palette(grey);

    &:hover,
    &:focus,
    &:active,
    &.is-active {
      text-decoration: underline;
      color: palette(grey, dark);
    }
  }
}

%c-accordion,
.c-accordion {
  clear: both;
  margin-bottom: ru(0.75);
}

%c-accordion__label,
.c-accordion__label {
  @extend %u-clearfix;
  display: block;
  position: relative;
  outline: none;
  background-color: palette(grey, xlight);
  cursor: pointer;
  padding: ru(0.75) ru(3) ru(0.75) ru(0.75);
  text-transform: uppercase;
  text-decoration: none;
  color: palette(grey, dark);
  font-family: $title-font-family;

  &:focus,
  &:hover,
  &:active {
    border-color: palette(grey);
    background-color: palette(grey, light);
  }
}

%c-accordion__label-icon,
.c-accordion__label-icon {
  @include svg-centered (ru(1.5), ru(1.5), ru(2), ru(2));
  // Vertical center w/ Transform y
  position: absolute;
  top: 50%;
  right: ru(0.25);
  transform: translateY(-50%);
  stroke: palette(grey, dark);
  fill: none;
}

%c-accordion__svg--expand,
.c-accordion__svg--expand {
  display: none;

  // STOP FOUC - js is running and it is not open - Show Icon
  .js .c-accordion:not([data-accordion-state='is-open']) &,
  .c-accordion[data-accordion-state='is-closed'] &,
  .no-js & {
    display: block;
  }
}

%c-accordion__svg--collapse,
.c-accordion__svg--collapse {
  display: none;

  .c-accordion[data-accordion-state='is-open'] & {
    display: block;
  }
}

%c-accordion__label-text,
.c-accordion__label-text {
  // pad top of text box to compensate for optical padding
  // Caused by using all caps DIN
  padding-top: ru(0.125);
}

%c-accordion__content,
.c-accordion__content {
  @extend %u-remove-child--margin-top-bottom;
  position: relative;
  background-color: palette(grey, mediumlight);
  padding: ru(0.75);
  color: $white;

  // STOP FOUC - js is running and it is not open (doesn't need close state)
  .js .c-accordion:not([data-accordion-state='is-open']) &,
  .c-accordion[data-accordion-state='is-closed'] & {
    display: none;
  }
}

@import 'layouts/l-eq';
@import 'layouts/l-page';
@import 'layouts/l-form';

// Other Layout Naming Examples
// @import 'layouts/l-header';
// @import 'layouts/l-page';
// @import 'layouts/l-2col';
// @import 'layouts/l-5col';
// @import 'layouts/l-megamenu';
// @import 'layouts/l-footer';
// @import 'layouts/l-form';

@import 'utility/u-remove-child';


// Vendor
// @import 'vendor/system.base';
// @import 'vendor/remodal';
// @import 'vendor/slidebar';
// @import 'vendor/slick';

%u-clearfix,
.u-clearfix {

  &:before,
  &:after {
    display: table;
    content: '';
  }

  &:after {
    clear: both;
  }
}

// Foreground Colors
%u-color--fg-white,
.u-color--fg-white {
  color: $white;
}

%u-color--fg-grey,
.u-color--fg-grey {
  color: palette(grey);
}

// Background Colors
%u-color--bg-white,
.u-color--bg-white {
  background-color: $white;
}

%u-color--bg-grey,
.u-color--bg-grey {
  background-color: palette(grey);
}

%u-color--bg-grey-light,
.u-color--bg-grey-light {
  background-color: palette(grey, light);
}

%u-color--bg-grey-xlight,
.u-color--bg-grey-xlight {
  background-color: palette(grey, xlight);
}

// Basic styling used in muliple lists

// Intended for Drupals default menu (ul.menu) / tabs formats

%u-inline-links__list {
  margin: 0;
  padding: 0;
  list-style-type: none;
}

%u-inline-links__list-item {
  float: left;
  margin: 0 ru(0.25) 0 0;
}

%u-inline-links {
  @extend %u-inline-links__list;

  li {
    @extend %u-inline-links__list-item;
  }
}

// Reset to Default List Style
%u-list--reset {
  margin: 0;
  padding: 0 0 0 ru(1);
  list-style-type: none;
  list-style-position: outside;
}

// Reset to block-like list with no padding / margins
%u-list__list--blank {
  margin: 0;
  padding: 0;
  list-style-type: none;
  list-style-position: outside;
}

%u-list__item--blank {
  margin: 0;
}

%u-list--blank {
  @extend %u-list__list--blank;

  li {
    @extend %u-list__item--blank;
  }
}

%u-max-width {
  margin: 0 auto;
  max-width: 1340px;
}

@mixin text-hide {
  overflow: hidden;
  text-indent: 120%;
  white-space: nowrap;
}

%u-text-hide,
.u-text-hide {
  @include text-hide;
}

%visually--hidden,
.visuallyhidden,
.u-visually--hidden {
  @include visuallyhidden;
}

%visually--shown,
.visuallyshown,
.u-visually--shown {
  @include visuallyshown;
}

$transition-duration: 0.25s !default;

%u-animate-background-color {
  transition-property: background-color;
  transition-duration: $transition-duration;
}

%u-animate-color {
  transition-property: color;
  transition-duration: $transition-duration;
}

%u-animate-opacity {
  transition-property: opacity;
  transition-duration: $transition-duration;
}

%u-animate-all {
  transition-property: all;
  transition-duration: $transition-duration;
}

