@import 'global/g-type/g-type__variables';
@import 'global/vendor/normalize';
@import 'global/g--functions/g--functions';
@import 'global/g--mixins/g--mixins';
@import 'global/g-grid-breakpoint/g-grid-breakpoints';
@import 'global/g-color/g-color';
@import 'global/g-html/g-html';
@import 'global/g-type/g-type';
@import 'global/g-link/g-link';
@import 'global/g-image/g-image';
@import 'global/g-list/g-list';
@import 'global/g-svg/g-svg';
@import 'global/g-form/g-form';
// @import 'global/g-table/g-table';
// @import 'global/print';

@import 'px-to-rem';
@import 'strip-unit';
@import 'rhythm-unit';

$base-font-size: 16px !default;

@function em($px, $base: $base-font-size) {
  @return ($px / $base) * 1em;
}

@function rem($px, $base: $base-font-size) {
  @return ($px / $base) * 1rem;
}

@function strip-unit($num) {
  @return $num / ($num * 0 + 1);
}

$base-line-height: 24px !default;

@function ru($rhythm-units: 1) {
  @return rem($base-line-height * $rhythm-units);
}

@import 'debug-layout';
// @import 'placeholder';
// @import 'rwd-typography';
// @import 'svg-position';
// @import 'show-hide';
@import 'photoshop-drop-shadow';

// Variables
$debug-colors: rgb(160, 0, 0), rgb(0, 160, 0), rgb(0, 0, 160) !default; // scss-lint:disable ColorVariable

// Global variable for counting runs
$debug-count: 1 !default !global;

@mixin dbl ($i: $debug-count) {
  $color: nth($debug-colors, $i);
  outline: 2px dashed $color;
  background-color: rgba($color, 0.5);

  @if ($i < length($debug-colors)) {
    $debug-count: $i + 1 !global;
  }@else {
    $debug-count: 1 !global;
  }
}

//--------------------------------
// Photoshop Drop Shadow
//--------------------------------
@mixin photoshop-drop-shadow ($angle: 0, $distance: 0, $spread: 0, $size: 0, $color: $black, $inner: false) {
  $angle: (180 - $angle) * pi() / 180; // convert to radians
  $h-shadow: round(cos($angle) * $distance);
  $v-shadow: round(sin($angle) * $distance);
  $css-spread: $size * $spread / 100;
  $blur: ($size - $css-spread);
  $inset: if($inner != false, 'inset', '');
  @include box-shadow($h-shadow $v-shadow $blur $css-spread $color unquote($inset));
}

//--------------------------------
// Photoshop Inner Shadow
//--------------------------------
@mixin photoshop-inner-shadow ($angle: 0, $distance: 0, $spread: 0, $size: 0, $color: $black) {
  @include photoshop-drop-shadow ($angle, $distance, $spread, $size, $color, true);
}

//--------------------------------
// Photoshop Text Shadow
//--------------------------------
@mixin photoshop-text-shadow ($angle: 0, $distance: 0, $spread: 0, $size: 0, $color: $black) {
  // NOTE: $spread has no effect for text shadows
  $angle: (180 - $angle) * pi() / 180;
  $h-shadow: round(cos($angle) * $distance);
  $v-shadow: round(sin($angle) * $distance);
  $css-spread: $size * $spread / 100;
  $blur: ($size - $css-spread);

  @include text-shadow($h-shadow $v-shadow $blur $color);
}

// Breakpoint Variables
$breakpoint-no-query-fallbacks: true;
$breakpoint-to-ems: true;

// Don't forget to update the theme .info file breakpoints!

// Respond-to Syntax creates a map of breakpoints named after UI changes
$breakpoints: add-breakpoint('expanded-wrapper', 400px);
$breakpoints: add-breakpoint('type-large', 600px);
$breakpoints: add-breakpoint('full-nav', 900px);
$breakpoints: add-breakpoint('max-size', 1200px);

// Range Breakpoints
//
// $bp-default-to-sml: 0 ($bp-med - 1px);
// $bp-default-to-med: 0 ($bp-lrg - 1px);
// $bp-med-to-lrg: $bp-med ($bp-lrg - 1px);
// $bp-sml-to-max: $bp-sml $bp-huge;

// Singularity Variables
// https://github.com/Team-Sass/Singularity/wiki/Creating-Grids
//


//Defines default 2 column grid
@include add-grid(12);
// @include add-grid(1 2 at $bp-med);
// @include add-grid(2 4 1 at $bp-lrg);
@include add-gutter-style('fixed');
@include add-gutter-style('split');
@include add-gutter(0);


// // Must enable debug setting
// @include sgs-change('debug', true);

@import 'g-color__functions';

// Static Colors
$black: #030303;
$white: #fefefe;


// Base Palette

$color-grey-base: #939598;
$color-blue-base: #3f3fb1;


$palettes: (
  grey: (
    base: $color-grey-base,
    xdark: darken($color-grey-base, 30%),
    dark: darken($color-grey-base, 20%),
    light: lighten($color-grey-base, 10%),
    xlight: lighten($color-grey-base, 20%),
  ),

  blue: (
    base: #3f3fb1,
    dark: #2b3078,
    light: #7c7fb1,
    xlight: #cce0ff
  )
);



// Shadow Color
$shadow: rgba($black, 0.5);

// Array of colors for /global/mixin/_debug-layout.scss
$debug-blue: rgb(0, 0, 160);
$debug-red: rgb(160, 0, 0);
$debug-green: rgb(0, 160, 0);

$debug-colors: $debug-red, $debug-green, $debug-blue !global;

$color-base-grey: rgb(229, 231, 234) !default;
// scss-lint:disable ColorVariable
$palettes: (
  purple: (
    base: rgb(42, 40, 80),
    light: rgb(51, 46, 140),
    dark: rgb(40, 38, 65)
  ),
  grey: (
    base:  $color-base-grey,
    light: lighten($color-base-grey, 10%),
    dark: darken($color-base-grey, 10%)
  )
) !default;
// scss-lint:enable ColorVariable

@function palette($palette, $tone: 'base') {
  // Report Error if calling missing color

  @if map-has-key(map-get($palettes, $palette), $tone) {
    @return map-get(map-get($palettes, $palette), $tone);
  } @else {
    @error $tone + ' is not a valid key in palettes';
  }
}

@viewport {
  width: device-width;
}

html {
  line-height: ($base-line-height / $base-font-size) * 1em;
  font-family: Arial, 'Helvetica Neue', Helvetica, sans-serif;
  font-size: ($base-font-size / 16px) * 100%;
  -ms-text-size-adjust: 100%;
  -webkit-text-size-adjust: 100%;
}


body {
  // Use Flex display to allow footer (or body) to scale full hieght of viewport
  display: flex;
  flex-direction: column;
  // Remove UA default margin.
  margin: 0;
  height: 100%;
  min-height: 100vh;
}

// GLOBAL TYPE COMPONENTS

// NOTE: Variables partial is imported in style.scss before all other global functions
// $base-font-size and $base-line-height are needed for global functions like rem()
// @import 'g-type__variables';

@import 'g-type__functions';

// Optional Partial - if you need to import font-face
@import 'g-type__fonts';

html {
  line-height: ($base-line-height / $base-font-size) * 1em;
  color: palette(grey, xdark);
  font-family: $base-font-family;
  font-size: ($base-font-size / 16px) * 100%;
  -ms-text-size-adjust: 100%;
  -webkit-text-size-adjust: 100%;
}

@mixin font-size($fs-map, $fs-breakpoints: $breakpoints) {
  @each $fs-breakpoint, $fs-font-size in $fs-map {
    @if $fs-breakpoint == null {
      @include make-font-size($fs-font-size);
    } @else {
      // If $fs-font-size is a key that exists in
      // $fs-breakpoints, use the value
      @if map-has-key($fs-breakpoints, $fs-breakpoint) {
        $fs-breakpoint: map-get($fs-breakpoints, $fs-breakpoint);
      }

      @include breakpoint ($fs-breakpoint) {
        @include make-font-size($fs-font-size);
      }
    }
  }
}

// Utility function for mixin font-size
@mixin make-font-size($fs-font-size) {
  // If $fs-font-size is a list, include
  // both font-size and line-height
  @if type-of($fs-font-size) == 'list' {
    $fontsize: nth($fs-font-size, 1);
    // If a px unit convert to Rem
    @if (unit($fontsize) == 'px') {
      font-size: rem($fontsize);
    } @else {
      line-height: $fontsize;
    }
    @if (length($fs-font-size) > 1) {
      $lineheight: nth($fs-font-size, 2);
      @if (unit($lineheight) == 'px') {
        line-height: strip-unit($lineheight / $fontsize);
      } @else {
        line-height: $lineheight;
      }
    }
  } @else {
    font-size: $fs-font-size;
  }
}

$base-font-family: 'Roboto', 'Arial', sans-serif;

$title-font-family: 'Roboto Slab', 'Arial', sans-serif;
$title-font-weight: 700;

$base-font-size: 16px;
$base-line-height: 26px;


// Default text elements
$p--xsmall-font-sizes: (
  null: (12px, $base-line-height * 0.75)
);

$p--small-font-sizes: (
  null: (14px, $base-line-height * 0.75)
);

$p-font-sizes: (
  null: (16px, $base-line-height)
);

$p--large-font-sizes: (
  null: (18px, $base-line-height)
);

$p--xlarge-font-sizes: (
  null: (20px, $base-line-height * 1.25)
);

$p--xxlarge-font-sizes: (
  null: (24px, $base-line-height * 1.25)
);


// Heading text elements
$h1-font-sizes: (
  null: (32px, $base-line-height * 1.5),
  type-large: (42px, $base-line-height * 2)
);

$h2-font-sizes: (
  null: (28px, $base-line-height * 1.25),
  type-large: (36px, $base-line-height * 1.75)
);

$h3-font-sizes: (
  null: (24px, $base-line-height * 1.25),
  type-large: (32px, $base-line-height * 1.5)
);

$h4-font-sizes: (
  null: (21px, $base-line-height * 1),
  type-large: (28px, $base-line-height * 1.25)
);

$h5-font-sizes: (
  null: (18px, $base-line-height * 1),
  type-large: (22px, $base-line-height * 1.25)
);

$h6-font-sizes: (
  null: (16px, $base-line-height * 1),
  type-large: (18px, $base-line-height * 1)
);

// NOTE: For IE9+ only woff is needed.
// See https://webdevstudios.com/2016/05/26/css-font-face-2016-edition/

// Roboto Regular
@include font-face("Roboto", (
  // 'ttf': '/fonts/roboto/RobotoSlab-Regular-webfont',
  'woff': '/fonts/roboto/RobotoSlab-Regular-webfont'
));

@include font-face("Roboto", (
  // 'ttf': '/fonts/roboto/Roboto-Italic-webfont',
  'woff': '/fonts/roboto/Roboto-Italic-webfont'
), italic);

@include font-face("Roboto", (
  // 'ttf': '/fonts/roboto/Roboto-Bold-webfont',
  'woff': '/fonts/roboto/Roboto-Bold-webfont'
), bold);

@include font-face("Roboto Slab", (
  // 'ttf': '/fonts/roboto_slab/RobotoSlab-Bold-webfont.woff',
  'woff': '/fonts/roboto_slab/RobotoSlab-Bold-webfont.woff'
), bold);

%g-type__core {
  @include font-size($p-font-sizes);
  margin: 0 0 1rem 0;
}

p {
  @extend %g-type__core;
}

%g-type--small,
.g-type--small {
  @include font-size($p--small-font-sizes);
}

%g-type--large,
.g-type--large {
  @include font-size($p--large-font-sizes);
}

%g-type--xlarge,
.g-type--xlarge {
  @include font-size($p--xlarge-font-sizes);
}


@import 'g-type--headline';

%heading-font {
  @extend %g-type__core;
  margin: 1rem 0 0.5rem;
  font-family: $title-font-family;
  font-weight: $title-font-weight;
}

h1,
.g-type__h1,
%g-type__h1 {
  @extend %heading-font;
  @include font-size($h1-font-sizes);
}

h2,
.g-type__h2,
%g-type__h2 {
  @extend %heading-font;
  @include font-size($h2-font-sizes);
}

h3,
.g-type__h3,
%g-type__h3 {
  @extend %heading-font;
  @include font-size($h3-font-sizes);
}

h4,
.g-type__h4,
%g-type__h4 {
  @extend %heading-font;
  @include font-size($h4-font-sizes);
}

h5,
.g-type__h5,
%g-type__h5 {
  @extend %heading-font;
  @include font-size($h5-font-sizes);
}

h6,
.g-type__h6,
%g-type__h6 {
  @extend %heading-font;
  @include font-size($h6-font-sizes);
}

// Reset Links
a {
  text-decoration: none;
}


%g-link--core {
  @extend %u-animate-all;
  display: inline-block;
  background-repeat: repeat-x;
  background-position: bottom center; // Default for old safari
  background-position: bottom 0.25em center; //sass-lint:disable-line no-duplicate-properties
  background-size: 100% 1px;
}

%g-link,
.g-link {
  @extend %g-link--core;

  // Creates a "shadow" the same color as the background to allow text to cover the underline
  $text-background-color: $white;
  $underline: palette(blue, light);

  background-color: $text-background-color;
  background-image: linear-gradient(to right, $underline 0%, $underline 100%);
  color: palette(blue, dark);
  // You might need to play with the shadow sizes for different fonts
  text-shadow: 1px 0 0 $text-background-color, -1px 0 0 $text-background-color, 0 0.5px 0 $text-background-color, 0 -0.5px 0 $text-background-color;

  &:visited {
    color: palette(blue, dark);
  }

  &:hover,
  &:active {
    $text-background-color: palette(grey, xlight);
    background-color: $text-background-color;
    color: palette(blue, dark);
    text-shadow: 1px 0 0 $text-background-color, -1px 0 0 $text-background-color, 0 0.5px 0 $text-background-color, 0 -0.5px 0 $text-background-color;
  }

  &:focus {
    outline: 0; // Only doing it as it's already contrasty
    background-color: palette(grey, xlight);
    color: palette(blue, dark);
    text-shadow: none;
  }
}

.g-link--grey-dark {
  @extend %g-link--core;

  // Creates a "shadow" the same color as the background to allow text to cover the underline
  $text-background-color: $white;
  $underline: palette(blue, light);

  background-color: $text-background-color;
  background-image: linear-gradient(to right, $underline 0%, $underline 100%);
  color: palette(grey, dark);
  // You might need to play with the shadow sizes for different fonts
  text-shadow: 1px 0 0 $text-background-color, -1px 0 0 $text-background-color, 0 0.5px 0 $text-background-color, 0 -0.5px 0 $text-background-color;

  &:visited {
    color: palette(grey, dark);
  }

  &:hover,
  &:active {
    $text-background-color: palette(blue, xlight);
    background-color: $text-background-color;
    color: palette(grey, xdark);
    text-shadow: 1px 0 0 $text-background-color, -1px 0 0 $text-background-color, 0 0.5px 0 $text-background-color, 0 -0.5px 0 $text-background-color;
  }

  &:focus {
    outline: 0; // Only doing it as it's already contrasty
    background-color: palette(grey, xlight);
    color: palette(grey, xdark);
    text-shadow: none;
  }



}

ol {
  margin: 0;
  padding: 0;
  list-style-type: none;
}

%g-list--ordered,
.g-list--ordered {
  margin-top: ru(0.25);
  margin-bottom: ru(0.5);
  padding-left: 0;
  list-style-type: none;
  counter-reset: list;


  > li {
    position: relative;
    margin-bottom: ru(0.25);
    margin-left: ru(1.25);
    padding-left: 0;
    list-style: none;

    &:before {
      position: absolute;
      top: 0;
      left: ru(-1);
      color: palette(blue);
      content: counter(list) '.';
      counter-increment: list;
    }
  }

  // Add in space above next paragraph (titles add their own top margin)
  + p {
    margin-top: ru(1);
  }
}

// Reset Lists to have no styling

ul {
  margin: 0;
  padding: 0;
  list-style-type: none;
}

%g-list--unordered,
.g-list--unordered {
  margin-top: ru(0.25);
  margin-bottom: ru(0.5);
  padding-left: 0;
  list-style-type: none;

  li {
    position: relative;
    margin-bottom: ru(0.25);
    margin-left: ru(1.25);
    padding-left: 0;
  }

  li:before {
    position: absolute;
    top: 0;
    left: ru(-1);
    color: palette(blue, light);
    content: '\2022 '; // Bullet symbol
  }

  ul {
    margin-bottom: 0;
   }
}

img,
figure {
  display: block;
}

// Core Extendables
%g-image--core {
  display: block;
  padding-top: ru(0.25);
  padding-bottom: ru(0.75);
  width: 100%;
  font-size: $base-font-size;
}

// Default img / figure Style
.g-image {
  @extend %g-image--core;
}


.g-image figcaption {
  background-color: palette(grey, xlight);
  padding: ru(0.25) ru(0.5);
  font-style: italic;
}

// Import Variations
@import 'g-image--left';
@import 'g-image--right';
@import 'g-image--center';

.image-content-half-width-left,
.align-left {
  @extend %g-image--core;

  @include respond-to('type-large') {
    float: left;
    margin-right: ru(0.5);
    padding-right: ru(0.5);
    width: 50%;
  }

  //Add if width changes @ breakpoints
  // @include respond-to('2-up') {
  //   width: 45%;
  // }

  // @include respond-to('max-size') {
  //   width: 35%;
  // }
}

.image-content-half-width-right,
.align-right {
  @extend %g-image--core;

  @include respond-to('type-large') {
    float: right;
    margin-left: ru(0.5);
    padding-left: ru(0.5);
    width: 50%;
  }

  //Add if width changes @ breakpoints
  // @include respond-to('2-up') {
  //   width: 45%;
  // }

  // @include respond-to('max-size') {
  //   width: 35%;
  // }
}

.image-content-half-width-center,
.align-center {
  @extend %g-image--core;

  @include respond-to('type-large') {
    margin: 0 auto;
    padding: 0 ru(0.5) ru(1);
    width: 50%;
  }

  //Add if width changes @ breakpoints
  // @include respond-to('2-up') {
  //   width: 45%;
  // }

  // @include respond-to('max-size') {
  //   width: 35%;
  // }
}

svg {

  // Overrides presentation attributes in SVG file to
  // Allow these values to inherit from CSS file.

  // path,
  // line,
  // circle {
  //   fill: inherit;
  //   stroke: inherit;
  //   stroke-width: inherit;
  // }

  // Fix for Edge not bubbling up events on clicking the use in an svg.
  // https://connect.microsoft.com/IE/feedback/details/796745/mouse-events-are-not-delivered-at-all-anymore-when-inside-an-svg-a-use-is-removed-from-the-dom
  use {
    pointer-events: none;
  }
}

// Sets SVG to use "css color" as the fill in the SVG
.svg--fill {
  fill: currentColor;
}

// Sets SVG to use "css color" as the stroke in the SVG
.svg--stroke {
  stroke: currentColor;
}

// Scale in proportion, fluid width SVGs
// Allowing fully fluid SVG's
// Based on http://tympanus.net/codrops/2014/08/19/making-svgs-responsive-with-css/

%svg__wrapper {
  position: relative;
  width: 100%;
  height: 0;
}

// Extend the Actual SVG with placeholder
%svg__core,
.svg__core {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}

// // Apply this mixin to the svg's wrapper
@mixin svg-wrapper($intristic-ratio) {
  @extend %svg__wrapper;
  @include ir($intristic-ratio);
}

// Default Base line for positioning SVGs next to text
$baseline: 1.5rem !default;

@mixin svg-centered(
  $svg-width,
  $svg-height,
  $wrapper-width,
  $wrapper-height,
  $svg-class: 'g-svg',
  $x-offset: 0,
  $y-offset: 0,
  $layout-mode: 'margin') {

  @if ($wrapper-width < $svg-width or $wrapper-height < $svg-height) {
    @error ('Wrapper can not be larger the the svg');
  }

  @if ($x-offset != 0 and unit($x-offset) != '%') {
    @error ('$x-offset be a % unit');
  }

  // Define SVG Wrapper
  position: relative;
  width: $wrapper-width;
  height: $wrapper-height;

  // Define SVG
  .#{$svg-class} {
    position: absolute;
    top: 50% + $y-offset;
    left: 50% + $x-offset;
    width: $svg-width;
    height: $svg-height;

    @if ($layout-mode == 'transform') {
      transform: translateX(-50%) translateY(-50%);

    } @elseif ($layout-mode == 'margin') {
      margin-top: -1 * ($svg-height / 2);
      margin-left: -1 * ($svg-width / 2);

    } @else {
      @error ('layout-mode must be transform or margin');
    }
  }
}

// Prefix icon with a mult-line link with vertically centered (middle or top) svg to the left
// Apply to parent container with an SVG of the specified class inside

@mixin svg-inline(
  $baseline,
  $svg-width,
  $svg-height,
  $svg-pad-right,
  $svg-pad-left,
  $container-vertical-padding,
  $container-padding-right: 0,
  $container-padding-left: 0,
  $svg-class: 'g-svg',
  $h-alignment: 'left',
  $v-alignment: 'middle') {

  position: relative;
  transform-style: preserve-3d;

  // Top and Bottom Padding of Container
  @if ($container-vertical-padding != 0) {
    padding-top: $container-vertical-padding;
    padding-bottom: $container-vertical-padding;
  }

  @if ($h-alignment == 'left') {
    padding-left: $svg-pad-left + $svg-width + $svg-pad-right + $container-padding-left;

    @if ($container-padding-right != '0') {
      padding-right: $container-padding-right;
    }

  } @elseif ($h-alignment == 'right') {
    padding-right: $svg-pad-left + $svg-width + $svg-pad-right + $container-padding-right;

    @if ($container-padding-left != '0') {
      padding-left: $container-padding-left;
    }
  } @else {
    @error 'invalid h-alignment value';
  }


  // SVG positioning
  .#{$svg-class} {
    display: block;
    position: absolute;
    width: $svg-width;
    height: $svg-height;
    transform: translateX(-50%) translateY(-50%);
    transform-style: preserve-3d;

    @if ($h-alignment == 'left') {
      left: 0 + $svg-pad-left + $container-padding-left;
    } @else {
      right: 0 + $svg-pad-right;
    }

    // SVG vertical positioning

    // Vertical Align icon in middle of parent
    @if ($v-alignment == 'middle') {
      top: 50%;
    }
    @if ($v-alignment == 'top') {
      top: ($baseline / 2) + $container-vertical-padding;
    }

  }
}

// Core Extendables
%g-input--core {
  display: block;
  margin-top: ru(0.5);
  margin-bottom: ru(1);
  outline: 0;
  border: 1px solid palette(grey, light);
  border-radius: 4px;
  background: none;
  cursor: pointer;
  padding: ru(0.5);
  width: 100%;
  vertical-align: top;
  font-family: $base-font-family;
  // Many Resets for various browsers
  text-shadow: none;
  -webkit-appearance: none;

  &:focus {
    border-color: palette(grey, dark);
    background-color: $white;
  }

  // Error state
  &.has-error {
    // Change to sites red variant
    border-color: $debug-red;
    background-color: rgba($debug-red, 0.1);
    color: $debug-red;
  }

  &[disabled] {
    border-color: palette(grey, light);
    background-color: palette(grey, light);
  }
}

// Import Variations
@import 'g-form--input';
@import 'g-form--radio-check';

// Mixins

// Mixin for setting html5 placeholder color
$grey-light: #999999 !default;

@mixin placeholder($color: $grey-light) {
  &::-webkit-input-placeholder {
    color: $color;
  }

  /* Firefox 18- */
  &:-moz-placeholder {  // scss-lint:disable VendorPrefix
    color: $color;
  }

  /* Firefox 19+ */
  &::-moz-placeholder {   // scss-lint:disable VendorPrefix
    color: $color;
  }

  &:-ms-input-placeholder {
    color: $color;
  }
}

@mixin placeholder--core {
  @include placeholder();
}

// Extendables ***
// Overside Form Elements
%g-input--large {
  @extend %g-type--xlarge;
  margin-top: ru(1);
  border-radius: 12px;
  height: ru(3);
}

%g-input--large--centered {
  @extend %g-input--large;
  text-align: center;
}

%g-button--reset {
  border: 0;
  background-color: transparent;
  width: auto;
  border-spacing: 0;
  text-align: left;
  text-indent: 0;
  text-decoration: none;

  &::-moz-focus-inner,
  &::-moz-focus-inner {
    border: 0;
    padding: 0;
  }
}

label:not(.option) {
  @extend %g-type--xlarge;
  display: block;

  &.has-error {
    // Change to sites red variant
    border-color: $debug-red;
    background-color: rgba($debug-red, 0.1);
    color: $debug-red;
  }

  .form-required {
    color: $debug-red;
    font-weight: normal;
  }
}

// Text & Similar Inputs
textarea,
select,
input[type='date'],
input[type='datetime'],
input[type='datetime-local'],
input[type='email'],
input[type='month'],
input[type='number'],
input[type='password'],
input[type='search'],
input[type='tel'],
input[type='text'],
input[type='time'],
input[type='url'],
input[type='week'] {
  @extend %g-input--core;
  background-color: $white;

  &.g-input--large {
    @extend %g-input--large;
  }

  &.g-input--large--centered {
    @extend %g-input--large--centered;
  }

  &.sg-search-field {
    width: auto;
  }
}


// Removes select drop-down arrow
select {
  @extend %g-input--core;
  background: url('/svg/sprite-ui/arrow-down.svg') no-repeat right ru(0.5)center;
  padding: ru(0.375) ru(1.5) ru(0.375) ru(0.5);
  -moz-appearance: none !important; // sass-lint:disable-line no-important
}


// Placeholder Note we're repeating it on purpose!
// a group of selectors containing an invalid selector is invalid.
// http://stackoverflow.com/questions/2610497/change-an-inputs-html5-placeholder-color-with-css

//scss-lint:disable VendorPrefix
input::-webkit-input-placeholder,
textarea::-webkit-input-placeholder {  // Separate rule for Firefox. Separate rule for IE, too. Cannot stack with WebKit's.
  @include placeholder--core;
}

input:-moz-placeholder,
textarea:-moz-placeholder {
  @include placeholder--core;
}

input::-moz-placeholder,
textarea::-moz-placeholder {
  @include placeholder--core;
}

input:-ms-input-placeholder,
textarea:-ms-input-placeholder {
  @include placeholder--core;
}
//scss-lint:enable VendorPrefix

textarea {
  height: ru(6);
  overflow: auto;
  resize: vertical;
}

optgroup {

  &::-moz-focus-inner {  // Kill phantom spacing and dotted border that appears in Firefox.
    border: 0;
    padding: 0;
  }
}


//scss-lint:disable QualifyingElement
button,
input[type='reset'],
input[type='submit'],
input[type='button'] {
  @extend %g-button--reset;
  @extend %g-input--core;
  @extend %c-btn;
}
//scss-lint:enable QualifyingElement

// Search Input
///////////////
// Remove decoration from input
//scss-lint:disable QualifyingElement
input[type='search']::-webkit-search-decoration {  // UI Consistency
  display: none;
}
//scss-lint:enable QualifyingElement

// Radios & checkmarks
//////////////////////
// Remove shadows
//scss-lint:disable QualifyingElement
input[type='file'],
input[type='radio'],
input[type='checkbox'] {
  &:focus,
  &:active {
    box-shadow: none;
  }
}
//scss-lint:enable QualifyingElement

// Checkbox and radios (Drupal Styles)
%g-form__checkbox-radio-group--core {
  @extend %u-clearfix;
  margin-bottom: ru(2);
}

// Wrapper for each item
%g-form__checkbox-radio__item {
  position: relative;
  float: left;
  width: 100%;
}

// Element & label common styles
%g-form-check-radio--core {
  display: block;
  margin-top: ru(0.5);
  margin-bottom: (1);
  vertical-align: baseline;
  font-family: $base-font-family;
}

// Checkbox or radio UI component
%g-form-check-radio-element {
  @extend %g-form-check-radio--core;
  position: absolute;
  width: ru(1);
  height: ru(1);
}

// Label for each UI component
%g-form-check-radio-label {
  @extend %g-form-check-radio--core;
  float: left;
  padding-left: ru(1.5);
}


//Checkboxes
%g-form__checkbox-group,
.g-form-checkboxes {
  @extend %g-form__checkbox-radio-group--core;
}

%g-form__checkbox__item,
.g-form-type-checkbox {
  @extend %g-form__checkbox-radio__item;
}

%g-form__checkbox,
.g-form-checkbox {
  @extend %g-form-check-radio-element;
}

%g-form__checkbox-label,
.g-form-type-checkbox .option {
  @extend %g-form-check-radio-label;
}


//Radio Buttons
%g-form__radio-group,
.g-form-radios {
  @extend %g-form__checkbox-radio-group--core;
}

%g-form__radio__item,
.g-form-type-radio {
  @extend %g-form__checkbox-radio__item;
}

%g-form__radio,
.g-form-radio {
  @extend %g-form-check-radio-element;
}

%g-form__radio-label,
.g-form-type-radio .option {
  @extend %g-form-check-radio-label;
}

%g-form__radio-group,
.g-form-radios {
  @extend %g-form__checkbox-radio-group--core;
}

%g-form__radio__item,
.g-form-type-radio {
  @extend %g-form__checkbox-radio__item;
}

%g-form__radio,
.g-form-radio {
  @extend %g-form-check-radio-element;
}

%g-form__radio-label,
.g-form-type-radio .option {
  @extend %g-form-check-radio-label;
}

// Note: This breaks the naming conventions a bit. This should probably be
// a scoped class, but the assumptions is that no tables in the layout will
// Exist other then this style. This is expected in a wysiwyg editor that
// isn't easy to wrap all the elements in specific classes.

%g-table,
table {
  margin-top: 1rem;
  margin-bottom: 1rem;
  border-bottom: 1px solid palette(grey, light);
  width: 100%;
  height: 100%;
  border-collapse: collapse;
}

%g-table__cell--core {
  border: 1px solid palette(grey, light);
  padding: ru(0.25) ru(0.5);
}

// Header Cell
%g-table__th,
th {
  @extend %g-table__cell--core;
  @extend %g-type--large;
  background-color: palette(grey);
  padding-top: ru(0.5);
  padding-bottom: ru(0.5);
  text-align: left;
  color: $white;
  font-weight: 400;
}

// Default Cells
%g-table__td,
td {
  @extend %g-table__cell--core;
  vertical-align: top;
}

// Rows and Cells Styles
%g-table__tr,
tr {

  &:nth-child(even) {
    background-color: $white;
  }

  &:nth-child(odd) {
    background-color: palette(grey, xlight);
  }

  &:hover,
  &:focus {
    background-color: palette(grey, light);

    td {
      border-right-color: palette(grey);
      border-left-color: palette(grey, light);
    }
  }
}

// Column Variations
%g-table__col--narrow,
.g-table__col--narrow {
  min-width: ru(1);
  max-width: ru(2);
  text-align: center;
}

