// Mixins (none)


// Core Extendables
%c-btn--core {
  display: inline-block;
  border-width: 1px;
  border-style: solid;
  border-color: none;
  border-radius: rem(4px);
  text-transform: uppercase;
  text-decoration: none;
}

////
// Size Varations
////

%c-btn--default-padding {
  padding: ru(0.5) ru(1);
}

%c-btn--narrow-padding {
  padding: ru(0.25) ru(0.75);
}

////
// Color Variations
////
%c-btn--palette-grey {
  border-color: palette(grey, light);
  background-color: $white;
  color: palette(grey);

  &:hover,
  &:focus,
  &:active {
    border-color: palette(grey);
    background-color: palette(grey, xlight);
  }
}

%c-btn--palette-blue {
  border-color: palette(blue, dark);
  background-color: palette(blue);
  color: $white;

  &:hover,
  &:focus,
  &:active {
    border-color: palette(blue, dark);
    background-color: palette(blue, dark);
  }
}


// Button Variations
%c-btn,
.c-btn {
  @extend %c-btn--core;
  @extend %c-btn--default-padding;
  @extend %c-btn--palette-grey;
}

.c-btn--blue {
  @extend %c-btn--core;
  @extend %c-btn--default-padding;
  @extend %c-btn--palette-blue;
}

.c-btn--thin {
  @extend %c-btn--core;
  @extend %c-btn--narrow-padding;
  @extend %c-btn--palette-grey;
}

.c-btn--thin-blue {
  @extend %c-btn--core;
  @extend %c-btn--narrow-padding;
  @extend %c-btn--palette-blue;
}


// Import Variations
@import 'c-btn--icon';

%c-btn--icon-core {
  @extend %c-btn--core;
  @extend %u-animate-background-color; // Animate background
  position: relative;
  border-color: palette(grey, light);
  color: palette(grey, dark);
  fill: palette(grey, dark);

  &:hover,
  &:focus,
  &:active {
    background-color: palette(grey, xlight);
    color: palette(blue, dark);
    fill: palette(blue);
  }
}

%c-btn--icon,
.c-btn--icon {
  @extend %c-btn--icon-core;
  @include svg-centered(ru(1.25),  ru(1.25),  ru(1.5),  ru(1.5));
}


%c-btn--icon-large,
.c-btn--icon-large {
  @extend %c-btn--icon-core;
  @include svg-centered(ru(1.75),  ru(1.75),  ru(2),  ru(2));
}

.c-btn--icon-left {
  @extend %c-btn--icon-core;
  // See config/mixins/svg-position.scss
  @include svg-inline(
    $baseline: rem($base-line-height), // font baseline
    $svg-width: ru(0.75), // SVG width (units same as typography)
    $svg-height: ru(0.75), // SVG height
    $svg-pad-right: ru(0.25), // padding on right side of SVG
    $svg-pad-left: 0, // padding on left side of SVG
    $container-vertical-padding: ru(0.5), // Top & bottom padding for container
    $container-padding-left: ru(1), // Left padding for container
    $container-padding-right: ru(1), // Right padding for container    $svg-class: 'svg', // Class for the SVG inside the parent
    $svg-class: 'g-svg', // Class for the SVG inside the parent
    $h-alignment: 'left', // Align Icon on 'left' or 'right'
    $v-alignment: 'middle'); // Align Icon 'middle' or 'top'
}

.c-btn--icon-right {
  @extend %c-btn--icon-core;
  @include svg-inline(
    $baseline: rem($base-line-height), // font baseline
    $svg-width: ru(0.75), // SVG width (units same as typography)
    $svg-height: ru(0.75), // SVG height
    $svg-pad-right: 0, // padding on left side of SVG
    $svg-pad-left: ru(0.5), // padding on right side of SVG
    $container-vertical-padding: ru(0.5), // Top & bottom padding for container
    $container-padding-left: ru(1), // Left padding for container
    $container-padding-right: ru(0.75), // Right padding for container
    $svg-class: 'g-svg', // Class for the SVG inside the parent
    $h-alignment: 'right', // Align Icon on 'left' or 'right'
    $v-alignment: 'top'); // Align Icon 'middle' or 'top'
}

// Core Extendables
%c-logo,
.c-logo {
  width: ru(11.5);
}

%c-logo__svg-wrapper,
.c-logo__svg-wrapper {
  @include svg-wrapper(130 / 32); // Logo is 130x32px
}

.c-logo__svg {
  @extend %svg__core;
}

.c-logo__hidden-text {
  @extend %u-text-hide;
  height: 0;
}

.c-quote__wrapper {
  margin: ru(1) 0 ru(3) 0;
}

%c-quote,
.c-quote {
  @include font-size($h3-font-sizes);
  text-align: center;
  color: palette(blue, dark);
  font-weight: 700;

  p {
    @include font-size($h3-font-sizes);
  }
}


%c-quote--icon-core {
  @include svg-centered(ru(3),  ru(3),  ru(3),  ru(3));
  fill: palette(blue, xlight);

}

.c-quote__open {
  @extend %c-quote--icon-core;
}

.c-quote__close {
  @extend %c-quote--icon-core;
  margin-left: auto;
}


// %c-quote__cite,
// .c-quote__cite {
//   font-style: normal;
// }

.c-tabs {
  margin-top: ru(1);
}

.c-tabs__list {
  @extend %u-list--reset;
  margin: 0;
  padding: 0;
}

%c-tabs__list-item,
.c-tabs__list li {
  display: inline-block;
  margin: 0;
}

%c-tabs__btn,
.c-tabs a {
  @include font-size($p--small-font-sizes);
  display: block;
  border: 1px solid palette(grey);
  background-color: palette(grey, light);
  padding: ru(0.25) ru(0.5);
  color: palette(grey, dark);
  font-weight: 600;

  &:hover,
  &:focus,
  &:active,
  &.is-active {
    background-color: $white;
  }
}

.c-pager {
  @extend %u-clearfix;
  padding: ru(1) 0;
  text-align: center;

  @include respond-to('type-large') {
    padding-right: ru(1);
    padding-left: ru(1);
  }

  @include respond-to('full-nav') {
    padding-right: ru(0.5);
    padding-left: ru(0.5);
  }

  @include respond-to('max-size') {
    padding-right: 0;
    padding-left: 0;
  }
}

%c-pager__item-btn {
  display: inline-block;
  padding: 0 ru(1);
}

.c-pager__item-current {
  @extend %c-pager__item-btn;
}

.c-pager__item-prev {
  @extend %c-pager__item-btn;
}

.c-pager__item-next {
  @extend %c-pager__item-btn;
}

// EXTENDS

// Core list wrapper
%c-nav--core {
  margin: 0;
  padding: 0;
  list-style: none;
}

// Core list item
%c-nav__item--core {
  display: block;
  margin: 0;
}

@import 'c-nav--vertical';
@import 'c-nav--horizontal';
@import 'c-nav--inline';

.c-nav--vertical {
  @extend %c-nav--core;
}

.c-nav--vertical__item {
  @extend %c-nav__item--core;
}

.c-nav--vertical__link {
  display: block;
  padding: ru(0.25) 0;
  text-transform: uppercase;
  color: palette(grey);

  &:hover,
  &:focus,
  &:active {
    background-color: palette(grey, xlight);
    color: palette(grey, dark);
  }

  &.is-active {
    color: palette(grey, dark);
    font-weight: 700;
  }
}

.c-nav--horizontal {
  @extend %c-nav--core;
}

.c-nav--horizontal__item {
  @extend %c-nav__item--core;
  display: inline-block;

  &:not(:first-child) {
    padding-left: 1px;
  }

  &:not(:last-child) {
    padding-right: ru(0.25);
  }
}

.c-nav--horizontal__link {
  display: block;
  padding-top: 0;
  color: palette(grey);

  &:hover,
  &:focus,
  &:active {
    text-decoration: underline;
    color: palette(grey, dark);
  }

  &.is-active {
    color: palette(grey, dark);
  }
}

.c-nav--inline {
  @extend %u-list--reset;
  margin: 0;
  padding: 0;
  list-style: none;
  color: palette(grey);

  li {
    display: inline-block;
    margin: 0;

    &:not(:last-of-type)::after {
      padding-right: 3px;
      padding-left: ru(0.25);
      content: '»';
    }
  }

  a {
    color: palette(grey);

    &:hover,
    &:focus,
    &:active,
    &.is-active {
      text-decoration: underline;
      color: palette(grey, dark);
    }
  }
}

.c-accordion {
  clear: both;
  margin-bottom: ru(0.75);
}

.c-accordion__label {
  @extend %u-clearfix;
  display: block;
  position: relative;
  outline: none;
  background-color: palette(grey, xlight);
  cursor: pointer;
  padding: ru(0.75) ru(3) ru(0.75) ru(0.75);
  text-transform: uppercase;
  text-decoration: none;
  color: palette(grey, dark);
  font-family: $title-font-family;

  &:focus,
  &:hover,
  &:active {
    border-color: palette(grey);
    background-color: palette(grey, light);
  }
}

.c-accordion__label-icon {
  @include svg-centered (ru(1.5), ru(1.5), ru(2), ru(2));
  // Vertical center w/ Transform y
  position: absolute;
  top: 50%;
  right: ru(0.25);
  transform: translateY(-50%);
  stroke: palette(grey, dark);
  fill: none;
}

.c-accordion__svg--expand {
  display: none;

  // STOP FOUC - js is running and it is not open - Show Icon
  .js .c-accordion:not([data-accordion-state='is-open']) &,
  .c-accordion[data-accordion-state='is-closed'] &,
  .no-js & {
    display: block;
  }
}

.c-accordion__svg--collapse {
  display: none;

  .c-accordion[data-accordion-state='is-open'] & {
    display: block;
  }
}

.c-accordion__label-text {
  // pad top of text box to compensate for optical padding
  // Caused by using all caps DIN
  padding-top: ru(0.125);
}

.c-accordion__content {
  @extend %u-remove-child--margin-top-bottom;
  position: relative;
  background-color: palette(grey, dark);
  padding: ru(0.75);
  color: $white;

  // STOP FOUC - js is running and it is not open (doesn't need close state)
  .js .c-accordion:not([data-accordion-state='is-open']) &,
  .c-accordion[data-accordion-state='is-closed'] & {
    display: none;
  }
}

// Layout Extends
@import 'layouts/l--extends/l-extends';
