optgroup {

  &::-moz-focus-inner {  // Kill phantom spacing and dotted border that appears in Firefox.
    border: 0;
    padding: 0;
  }
}


//scss-lint:disable QualifyingElement
button,
input[type='reset'],
input[type='submit'],
input[type='button'] {
  @extend %g-button--reset;
  @extend %g-input--core;
  @extend %c-btn;
}
//scss-lint:enable QualifyingElement

// Search Input
///////////////
// Remove decoration from input
//scss-lint:disable QualifyingElement
input[type='search']::-webkit-search-decoration {  // UI Consistency
  display: none;
}
//scss-lint:enable QualifyingElement

// Radios & checkmarks
//////////////////////
// Remove shadows
//scss-lint:disable QualifyingElement
input[type='file'],
input[type='radio'],
input[type='checkbox'] {
  &:focus,
  &:active {
    box-shadow: none;
  }
}
//scss-lint:enable QualifyingElement

// Checkbox and radios (Drupal Styles)
%g-form__checkbox-radio-group--core {
  @extend %u-clearfix;
  margin-bottom: ru(2);
}

// Wrapper for each item
%g-form__checkbox-radio__item {
  position: relative;
  float: left;
  width: 100%;
}

// Element & label common styles
%g-form-check-radio--core {
  display: block;
  margin-top: ru(0.5);
  margin-bottom: (1);
  vertical-align: baseline;
  font-family: $base-font-family;
}

// Checkbox or radio UI component
%g-form-check-radio-element {
  @extend %g-form-check-radio--core;
  position: absolute;
  width: ru(1);
  height: ru(1);
}

// Label for each UI component
%g-form-check-radio-label {
  @extend %g-form-check-radio--core;
  float: left;
  padding-left: ru(1.5);
}


//Checkboxes
%g-form__checkbox-group,
.g-form-checkboxes {
  @extend %g-form__checkbox-radio-group--core;
}

%g-form__checkbox__item,
.g-form-type-checkbox {
  @extend %g-form__checkbox-radio__item;
}

%g-form__checkbox,
.g-form-checkbox {
  @extend %g-form-check-radio-element;
}

%g-form__checkbox-label,
.g-form-type-checkbox .option {
  @extend %g-form-check-radio-label;
}


//Radio Buttons
%g-form__radio-group,
.g-form-radios {
  @extend %g-form__checkbox-radio-group--core;
}

%g-form__radio__item,
.g-form-type-radio {
  @extend %g-form__checkbox-radio__item;
}

%g-form__radio,
.g-form-radio {
  @extend %g-form-check-radio-element;
}

%g-form__radio-label,
.g-form-type-radio .option {
  @extend %g-form-check-radio-label;
}

%g-form__radio-group,
.g-form-radios {
  @extend %g-form__checkbox-radio-group--core;
}

%g-form__radio__item,
.g-form-type-radio {
  @extend %g-form__checkbox-radio__item;
}

%g-form__radio,
.g-form-radio {
  @extend %g-form-check-radio-element;
}

%g-form__radio-label,
.g-form-type-radio .option {
  @extend %g-form-check-radio-label;
}

// Note: This breaks the naming conventions a bit. This should probably be
// a scoped class, but the assumptions is that no tables in the layout will
// Exist other then this style. This is expected in a wysiwyg editor that
// isn't easy to wrap all the elements in specific classes.

%g-table,
table {
  margin-top: 1rem;
  margin-bottom: 1rem;
  border-bottom: 1px solid palette(grey, light);
  width: 100%;
  height: 100%;
  border-collapse: collapse;
}

%g-table__cell--core {
  border: 1px solid palette(grey, light);
  padding: ru(0.25) ru(0.5);
}

// Header Cell
%g-table__th,
th {
  @extend %g-table__cell--core;
  @extend %g-type--large;
  background-color: palette(grey);
  padding-top: ru(0.5);
  padding-bottom: ru(0.5);
  text-align: left;
  color: $white;
  font-weight: 400;
}

// Default Cells
%g-table__td,
td {
  @extend %g-table__cell--core;
  vertical-align: top;
}

// Rows and Cells Styles
%g-table__tr,
tr {

  &:nth-child(even) {
    background-color: $white;
  }

  &:nth-child(odd) {
    background-color: palette(grey, xlight);
  }

  &:hover,
  &:focus {
    background-color: palette(grey, light);

    td {
      border-right-color: palette(grey);
      border-left-color: palette(grey, light);
    }
  }
}

// Column Variations
%g-table__col--narrow,
.g-table__col--narrow {
  min-width: ru(1);
  max-width: ru(2);
  text-align: center;
}

