.l-page__header-wrapper {

  @include dbl;
}

.l-page__header {
  @extend %l-extends__page-limit;
}

.l-page__main {
  @extend %l-extends__page-limit;
}

// .l-page__content {

// }

.l-page__footer-wrapper {

  @include dbl;
}

.l-page__footer {
  @extend %l-extends__page-limit;
}

@import 'utility/u-animation';
@import 'utility/u-clearfix';
@import 'utility/u-inline-links';
@import 'utility/u-list';
@import 'utility/u-remove-child';
@import 'utility/u-text-hide';
@import 'utility/u-visually--hidden';

%u-clearfix,
.u-clearfix {

  &:before,
  &:after {
    display: table;
    content: '';
  }

  &:after {
    clear: both;
  }
}

%u-inline-links__list {
  margin: 0;
  padding: 0;
  list-style-type: none;
}

%u-inline-links__list-item {
  float: left;
  margin: 0 ru(0.25) 0 0;
}

%u-inline-links {
  @extend %u-inline-links__list;

  li {
    @extend %u-inline-links__list-item;
  }
}

// Reset to Default List Style
%u-list--reset {
  margin: 0;
  padding: 0 0 0 ru(1);
  list-style-position: outside;
}

// Reset to block-like list with no padding / margins
%u-list__list--blank {
  margin: 0;
  padding: 0;
  list-style-type: none;
  list-style-position: outside;
}

%u-list__item--blank {
  margin: 0;
}

//TODO: Remove if posible
%u-list--blank {
  @extend %u-list__list--blank;

  li {
    @extend %u-list__item--blank;
  }
}

%u-remove-child--margin-top,
.u-remove-child--margin-top {

  > :first-child {
    margin-top: 0;
  }
}

%u-remove-child--margin-bottom,
.u-remove-child--margin-bottom {

  > :last-child {
    margin-bottom: 0;
  }
}

// Remove margins from first and last child element.
// Adding generic class to apply to wysiwyg fields to allow for consitent spacing in the page layouts.
%u-remove-child--margin-top-bottom,
.u-remove-child--margin-top-bottom {
  @extend %u-remove-child--margin-top;
  @extend %u-remove-child--margin-bottom;
}

@mixin text-hide {
  overflow: hidden;
  text-indent: 120%;
  white-space: nowrap;
}

%u-text-hide,
.u-text-hide {
  @include text-hide;
}

// Mixins
@mixin visually-hidden {
  position: absolute;
  margin: -1px;
  border: 0;
  padding: 0;
  width: 1px;
  height: 1px;
  overflow: hidden;
  clip: rect(0 0 0 0);

  .focusable {
    &:active,
    &:focus {
      position: static;
      margin: 0;
      width: auto;
      height: auto;
      overflow: visible;
      clip: auto;
    }
  }
}

@mixin visually-shown {
  position: static;
  margin: 0;
  width: auto;
  height: auto;
  overflow: visible;

  clip: auto;
  clip: rect(auto auto auto auto); // sass-lint:disable-line no-duplicate-properties
}

%u-visually--hidden,
.u-visually--hidden {
  @include visually-hidden;
}

%u-visually--shown,
.u-visually--shown {
  @include visually-shown;
}

$transition-duration: 0.25s !default;

%u-animate-background-color {
  transition-property: background-color;
  transition-duration: $transition-duration;
}

%u-animate-text-shadow {
  transition-property: text-shadow;
  transition-duration: $transition-duration;
}

%u-animate-color {
  transition-property: color;
  transition-duration: $transition-duration;
}

%u-animate-opacity {
  transition-property: opacity;
  transition-duration: $transition-duration;
}

%u-animate-all {
  transition-property: all;
  transition-duration: $transition-duration;
}

.u-word-break--pipe {
  display: block;

  @include respond-to('full-nav') {
    display: inline;

    &:not(:last-of-type)::after {
      padding-right: ru(0.25);
      padding-left: ru(0.25);
      content: '|';
    }

  }
}

// CKEDITOR's Default iframe class for the editor.
.s-wysiwyg,
.cke_editable { // sass-lint:disable-line class-name-format

  ul {
    @extend %g-list--unordered;
  }

  ol {
    @extend %g-list--ordered;
  }

  // Link Styles (unless custom btn styles added in WYSIWIYG)
  a:not(.c-btn) {
    @extend %g-link;
  }

}

